def calcular_tm(sequencia_dna):

  sequencia_dna = sequencia_dna.upper()

  num_a = sequencia_dna.count('A')
  num_t = sequencia_dna.count('T')
  num_g = sequencia_dna.count('G')
  num_c = sequencia_dna.count('C')

  comprimento_sequencia = len(sequencia_dna)

  if comprimento_sequencia == 0:
    return 0.0


  tm = 64.9 + 41 * ((num_g + num_c - 16.4)/comprimento_sequencia)
  return tm

sequencia_dna = ""
tm_calculada = calcular_tm(sequencia_dna)
print(f"A Tm calculada para a sequência de DNA é: {tm_calculada:.2f}°C")
